//Creating variables and arrays for letters//
var won = 0;
var lost = 0;
var attempts = 9;
var usedletters = [];
var letters = "qwertyuiopasdfghjklzxcvbnm";


//creates the random letter generated by the computer// 
function ranguess() {
    ranLetter = letters[Math.floor(Math.random() * letters.length)];
    console.log(ranLetter);
    //usedletters = [];// dont need this
}
ranguess();

//records what the player guesses//

//.onkeyup captures the players input as playerGuess
document.onkeyup = function (event) {
    var sofar = event.key;

//prevents the same key from being used//
    if (usedletters.indexOf(sofar) >= 0) {

    } else {
        //once an wrong letter has been used it is written on the HTML//
        usedletters.push(sofar);
        document.getElementById('sofar').innerHTML = usedletters;
        console.log(usedletters);

    }

    //if the letter guess was not equal to the random, then attempts decrease by 1, otherwise if right then wins increease and attempts are started over//
    if (sofar !== ranLetter) {
        attempts--;
    } else {
        won++;
        attempts = 9;
        usedletters = [];
        ranguess();
        alert("Congratulations!!! You guessed right!!!");
    }

    //After the attempts are used, losses increase by 1, attempts are rests, a new random letter is created//
    if (attempts === 0) {
        lost++;
        usedletters = [];
        attempts = 9;
        ranguess();
        alert("Oh No, You guessed wrong!! Try again!!");
    }

   
    //this writes the HTML file
    document.getElementById('wins').innerHTML = won;
    document.getElementById('losses').innerHTML = lost;
    document.getElementById('attempts').innerHTML = attempts;

}